import React, { Component } from 'react'
import './App.css'
import {getAName} from './components/usernames'
import {connect} from 'react-redux'
import Messages from './components/messages'
import Users from './components/users'
import loginService from './components/login'
import AuthBar from './components/authentication-bar'
import EmojiBox from './components/emoji'
import EmojiConvertor from 'emoji-js'

class App extends Component {
  state = {
    emoji: false,
  }

  componentDidMount() {
    const loggedUserJSON = window.localStorage.getItem('loggedUserJSON')
    const {dispatch} = this.props
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      dispatch({type: 'LOGIN', user})
    } else {
      const name = getAName()
      dispatch({type: 'username', name})
    }
  }

  sendIn = (event) => {
    event.preventDefault()
    const {dispatch, user, message} = this.props
    dispatch({type: 'chat message', msg: user+': '+message})
  }

  handleChange = (event) => {
    const {dispatch} = this.props
    dispatch({type: 'typing', msg: event.target.value})
  }

  handleNameChange = (event) => {
    const {dispatch} = this.props
    dispatch({type: 'TYPING_USERNAME', username: event.target.value})
  }

  handlePwChange = (event) => {
    const {dispatch} = this.props
    dispatch({type: 'TYPING_PASSWORD', password: event.target.value})
  }

  login = async (event) => {
    event.preventDefault()
    try {
      const {dispatch, username, password} = this.props
      const user = await loginService({
        username,
        password
      })

      dispatch({type: 'LOGIN', user})
      window.localStorage.setItem('loggedUserJSON', JSON.stringify(user))
    } catch (e) {
      const {dispatch} = this.props
      console.log('login failed')
      dispatch({type: 'FAILED_LOGIN'})
    }
  }

  logout = (event) => {
    const {dispatch} = this.props
    const name = getAName()
    dispatch({type: 'LOGOUT', name})
  }

  emojiClick = (code, emoji) => {
    const {dispatch, message} = this.props
    let emojiPic = new EmojiConvertor().replace_colons(`:${emoji.name}:`)
    dispatch({type: 'typing', msg: message+emojiPic})
  }

  toggleEmojiBox = () => {
    // TODO toggleEmoji
    this.setState({emoji: !this.state.emoji})
  }

  render() {
    const {message, messages, persons, user} = this.props
    return (
      <div className="App">
        <Messages messages={messages}/>
        <div id="rightBar">
          <AuthBar app={this} />
          <Users users={persons} user={user} />
        </div>
        <EmojiBox onEmojiClick={this.emojiClick} visible={this.state.emoji} />
        <form action="" onSubmit={this.sendIn} className="message">
          <input 
            id="m" 
            autoComplete="off" 
            value={message} 
            onChange={this.handleChange}
          />
          <div onClick={this.toggleEmojiBox} id="emoji"><i className="far fa-grin fa-2x"></i></div>
          <button>Send</button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  persons: state.persons,
  messages: state.messages,
  message: state.message,
  user: state.user,
  logged: state.logged,
  username: state.username,
  password: state.password,
  loggedUser: state.user
})

export default connect(mapStateToProps)(App)
