}

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      persons: [],
      latest: '',
      message: '',
      user: getAName()
    }
  }

  send = (event) => {
    event.preventDefault()
    socket.emit('chat message', this.state.user + ': '+this.state.message)
    this.setState({message: ''})
    socket.on('chat message', (msg) => {
      this.setState(prev => ({
        latest: msg
      }))
    })
  }

  handleChange = (event) => {
    this.setState({message: event.target.value})
  }


  render() {
    return (
      <div className="App">
        <ul id='messages'><Messages app={this} /></ul>
        <form action="" onSubmit={this.send}>
          <input 
            id="m" 
            autocomplete="off" 
            value={this.state.message} 
            onChange={this.handleChange}
          />
          <button>Send</button>
        </form>
      </div>
    );
  }
}

export default App;
