import React, { Component } from 'react'
import './App.css'
import {getAName} from './usernames'

const Messages = ({app}) => {
  return (
    <ul id='messages'>
      {app.state.messages.map(msg => 
        <li>{msg}</li>
      )}
    </ul>
  )
}

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      persons: [],
      messages: [],
      message: '',
      user: getAName()
    }
  }

  send = (event) => {
    event.preventDefault()
    socket.emit('chat message', this.state.user + ': '+this.state.message)
    this.setState({message: ''})
    /*
    socket.on('chat message', (msg) => {
      this.setState(prev => ({
        messages: [...prev.messages, msg]
      }))
    })
    */
  }

  handleChange = (event) => {
    this.setState({message: event.target.value})
  }

  render() {
    return (
      <div className="App">
        <Messages app={this} />
        <form action="" >
          <input 
            id="m" 
            autoComplete="off" 
            value={this.state.message} 
            onChange={this.handleChange}
          />
          <button onClick={this.send}>Send</button>
        </form>
      </div>
    );
  }
}

export default App;
