import React, { Component } from 'react'
import './App.css'
import {getAName} from './components/usernames'
import {connect} from 'react-redux'
import Messages from './components/messages'
import Users from './components/users'
import loginService from './components/login'

class App extends Component {
  componentDidMount() {
    const {dispatch} = this.props
    const name = getAName()
    dispatch({type: 'username', name})
  }

  sendIn = (event) => {
    event.preventDefault()
    const {dispatch, user, message} = this.props
    dispatch({type: 'chat message', msg: user+': '+message})
  }

  handleChange = (event) => {
    const {dispatch} = this.props
    dispatch({type: 'typing', msg: event.target.value})
  }

  handleNameChange = (event) => {
    const {dispatch} = this.props
    dispatch({type: 'TYPING_USERNAME', username: event.target.value})
  }

  handlePwChange = (event) => {
    const {dispatch} = this.props
    dispatch({type: 'TYPING_PASSWORD', password: event.target.value})
  }

  login = async (event) => {
    event.preventDefault()
    try {
      const {dispatch, username, password} = this.props
      const user = await loginService.login({
        username: username,
        password: password
      })

      dispatch({type: 'LOGIN', user})
    } catch (e) {
      const {dispatch} = this.props
      console.log('login failed')
      dispatch({type: 'LOGIN', user: null})
    }
  }

  AuthBar = () => {
    const {logged, username, password} = this.props
    if (logged) {
      return (
        <div></div>
      )
    }
    const redirect = window.location.hostname + "/api/login"
    return (
      <div>
        <form onSubmit={this.login}>
          Username:
          <input type="text" value={username} onChange={this.handleNameChange}></input>
          Password:
          <input type="password" value={password} onChange={this.handlePwChange}></input>
          <button>Log in</button>
        </form>

        <a href="/api/signup">Sign Up</a>
      </div>
    )
  }

  render() {
    const {message, messages, persons, logged, username, password} = this.props
    const toAuth = {logged, username, password}
    return (
      <div className="App">
        <this.AuthBar />
        <Messages messages={messages}/>
        <Users users={persons}/>
        <form action="" onSubmit={this.sendIn} className="message">
          <input 
            id="m" 
            autoComplete="off" 
            value={message} 
            onChange={this.handleChange}
          />
          <button >Send</button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  persons: state.persons,
  messages: state.messages,
  message: state.message,
  user: state.user,
  logged: state.logged,
  username: state.username,
  password: state.password,
  loggedUser: state.user
})

export default connect(mapStateToProps)(App);
