import io from 'socket.io-client'

const socket = io('http://localhost:3001')

const confSocket = ({app}) => {
    socket.on('chat message', msg => {
        app.setState(prev => ({
            messages: [...prev.messages, msg]
        }))
    })
}

export default confSocket
