import React, { Component } from 'react'
import './App.css'
import {getAName} from './usernames'
import {connect} from 'react-redux'
import {sendToServer} from './socket'

const Messages = ({app}) => {
  const {dispatch, messages} = app.props
  return (
    <ul id='messages'>
      {app.state.messages.map(msg => 
        <li>{msg}</li>
      )}
    </ul>
  )
}

class App extends Component {
  /*
  constructor(props) {
    super(props)
    this.state = {
      persons: [],
      messages: [],
      message: '',
      user: getAName()
    }
  }
  */
  componentDidMount() {
    const {dispatch} = this.props
    const name = getAName()
    dispatch({type: 'username', name})
  }

/*
  send = (event) => {
    event.preventDefault()
    socket.emit('chat message', this.state.user + ': '+this.state.message)
    this.setState({message: ''})

    socket.on('chat message', (msg) => {
      this.setState(prev => ({
        messages: [...prev.messages, msg]
      }))
    })

}*/

  sendIn = () => {
    const {dispatch, name, message} = this.props
    dispatch({type: 'chat message'})
    sendToServer(name+': '+message)
  }

  handleChange = (event) => {
    const {dispatch} = this.props
    dispatch({type: 'typing', msg: event.target.value})
  }

  render() {
    const {message} = this.props
    return (
      <div className="App">
        <Messages app={this} />
        <form action="" >
          <input 
            id="m" 
            autoComplete="off" 
            value={message} 
            onChange={this.handleChange}
          />
          <button onClick={this.sendIn}>Send</button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  persons: state.persons,
  messages: state.messages,
  message: state.message,
  user: state.user
})

export default connect(mapStateToProps)(App);
